import axios from "axios";

const API_URL = "http://192.168.228.138:5000/api/auth";

// 沺｢ ﾄ斉ハg nh蘯ｭp
export const login = async (email, password) => {
  try {
    const res = await axios.post(`${API_URL}/login`, { email, password });
    return res.data; // { token, role, ... }
  } catch (err) {
    const message =
      err.response?.data?.message || "ﾄ斉ハg nh蘯ｭp th蘯･t b蘯｡i, vui lﾃｲng th盻ｭ l蘯｡i";
    throw new Error(message);
  }
};

// 沺｢ ﾄ斉ハg kﾃｽ
export const register = async (formData) => {
  try {
    const res = await axios.post(`${API_URL}/register`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    return res.data; // { message, role }
  } catch (err) {
    const message =
      err.response?.data?.message || "ﾄ斉ハg kﾃｽ th蘯･t b蘯｡i, vui lﾃｲng th盻ｭ l蘯｡i";
    throw new Error(message);
  }
};
