// src/Pages/Register.js
import React, { useState } from "react";
import { register } from "../Api/auth";
import "../Styles/Register.css";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [image, setImage] = useState(null);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };

  const validatePassword = (pwd) => {
    if (pwd.length < 6) return "M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±";
    if (!/[A-Z]/.test(pwd)) return "M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 ch·ªØ in hoa (A-Z)";
    if (!/[a-z]/.test(pwd)) return "M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 ch·ªØ th∆∞·ªùng (a-z)";
    if (!/[!@#$%^&*(),.?\":{}|<>]/.test(pwd))
      return "M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 k√Ω t·ª± ƒë·∫∑c bi·ªát";
    return null;
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");


    
    if (!name || !email || !password || !confirmPassword || !phone || !image) {
      setError("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin v√† ch·ªçn ·∫£nh ƒë·∫°i di·ªán");
      return;
    }

    const pwdError = validatePassword(password);
    if (pwdError) {
      setError(pwdError);
      return;
    }

    if (password !== confirmPassword) {
      setError("M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp");
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("email", email);
      formData.append("password", password);
      formData.append("confirmPassword", confirmPassword);
      formData.append("phone", phone);
      formData.append("image", image);

      await register(formData);

      setSuccess("ƒêƒÉng k√Ω th√†nh c√¥ng!");
      setTimeout(() => {
        window.location.href = "/";
      }, 1500);
    } catch (err) {
      setError(err.message || "ƒêƒÉng k√Ω th·∫•t b·∫°i");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-wrapper">
      <div className="register-header">
        <h1 className="logo">SeatMe</h1>
        <p className="subtitle">ƒê·∫∂T B√ÄN ONLINE</p>
      </div>

      <div className="register-box">
        {error && <p className="error">{error}</p>}
        {success && <p className="success">{success}</p>}

        <form onSubmit={handleRegister}>
          <label>H·ªç t√™n</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />

          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />

          <label>M·∫≠t kh·∫©u</label>
          <div className="password-field">
            <input
              type={showPassword ? "text" : "password"}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button
              type="button"
              className="toggle-btn"
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </button>
          </div>

          <label>X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
          <div className="password-field">
            <input
              type={showConfirmPassword ? "text" : "password"}
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
            <button
              type="button"
              className="toggle-btn"
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              {showConfirmPassword ? "üôà" : "üëÅÔ∏è"}
            </button>
          </div>

          <label>S·ªë ƒëi·ªán tho·∫°i</label>
          <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />

          <label>·∫¢nh ƒë·∫°i di·ªán</label>
          <input type="file" accept="image/*" onChange={handleFileChange} />
          {image && (
            <img
              src={URL.createObjectURL(image)}
              alt="avatar preview"
              className="avatar-preview"
            />
          )}

          <button type="submit" disabled={loading}>
            {loading ? "ƒêang ƒëƒÉng k√Ω..." : "ƒêƒÉng k√Ω"}
          </button>
        </form>

        <p className="login-link">
          B·∫°n ƒë√£ c√≥ t√†i kho·∫£n? <a href="/">ƒêƒÉng nh·∫≠p</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
