// src/Pages/Register.js
import React, { useState } from "react";
import "../Styles/Register.css";

export default function Register() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [error, setError] = useState("");

  // Xử lý thay đổi input
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  // Validate password mạnh
  const validatePassword = (pwd) => {
    if (pwd.length < 6) return "Mật khẩu phải có ít nhất 6 ký tự";
    if (!/[A-Z]/.test(pwd)) return "Mật khẩu phải chứa ít nhất 1 chữ hoa";
    if (!/[0-9]/.test(pwd)) return "Mật khẩu phải chứa ít nhất 1 số";
    if (!/[!@#$%^&*(),.?":{}|<>]/.test(pwd))
      return "Mật khẩu phải chứa ít nhất 1 ký tự đặc biệt";
    return "";
  };

  // Submit form
  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");

    // Kiểm tra mật khẩu trùng khớp
    if (form.password !== form.confirmPassword) {
      setError("Mật khẩu xác nhận không khớp");
      return;
    }

    // Kiểm tra password mạnh
    const pwdError = validatePassword(form.password);
    if (pwdError) {
      setError(pwdError);
      return;
    }

    // ✅ Giả sử gọi API đăng ký
    console.log("Register success:", form);
    alert("Đăng ký thành công!");
  };

  return (
    <div className="register-container">
      <h2>Đăng ký</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Họ tên"
          value={form.name}
          onChange={handleChange}
          required
        />

        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />

        <input
          type="password"
          name="password"
          placeholder="Mật khẩu"
          value={form.password}
          onChange={handleChange}
          required
        />

        <input
          type="password"
          name="confirmPassword"
          placeholder="Xác nhận mật khẩu"
          value={form.confirmPassword}
          onChange={handleChange}
          required
        />

        <button type="submit">Đăng ký</button>
      </form>

      {error && <p className="error">{error}</p>}
    </div>
  );
}
