import React, { useState } from "react";
import { login } from "../Api/auth";
import '../styles/Login.css'; // ho·∫∑c '../css/Login.css'


const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      setError("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß email v√† m·∫≠t kh·∫©u");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const data = await login(email, password);
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));

      alert("ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      window.location.href = "/dashboard";
    } catch (err) {
      setError(err.message || "Email ho·∫∑c m·∫≠t kh·∫©u kh√¥ng ƒë√∫ng");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white flex flex-col">
      {/* Background ƒë·ªè chi·∫øm 35% */}
      <div className="bg-gradient-to-b from-red-800 to-red-600 h-[35vh]"></div>

      {/* Form tr·∫Øng */}
      <div className="bg-white rounded-t-[40px] shadow-lg -mt-16 p-8 max-w-md mx-auto w-full">
        {/* Logo */}
        <div className="text-center mb-6">
          <h1 className="text-3xl font-bold text-red-600">SeatMe</h1>
          <p className="text-red-600 text-sm tracking-widest">ƒê·∫∂T B√ÄN ONLINE</p>
        </div>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>}

        {/* Form */}
        <form onSubmit={handleLogin} className="space-y-5">
          <div>
            <label className="block text-red-600 mb-1">Email</label>
            <input
              type="email"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-red-600 mb-1">M·∫≠t kh·∫©u</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button
                type="button"
                className="absolute right-3 top-2 text-red-600"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
          </div>

          {/* Qu√™n m·∫≠t kh·∫©u */}
          <div className="text-right">
            <a href="/forgot-password" className="text-red-500 font-semibold">
              Qu√™n m·∫≠t kh·∫©u?
            </a>
          </div>

          {/* N√∫t ƒëƒÉng nh·∫≠p */}
          <button
            type="submit"
            className="w-full bg-gradient-to-r from-red-800 to-red-600 text-white font-bold py-3 rounded-full"
            disabled={loading}
          >
            {loading ? "ƒêang ƒëƒÉng nh·∫≠p..." : "ƒêƒÉng nh·∫≠p"}
          </button>
        </form>

        {/* ƒêƒÉng k√Ω */}
        <p className="text-center text-gray-600 mt-6">
          B·∫°n ch∆∞a c√≥ t√†i kho·∫£n?{" "}
          <a href="/register" className="font-bold text-black">
            ƒêƒÉng k√Ω
          </a>
        </p>
      </div>
    </div>
  );
};

export default Login;
