import React, { useState } from "react";
import { register } from "../Api/auth"; // H√†m g·ªçi API ƒëƒÉng k√Ω
import "../Styles/Register.css";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [avatar, setAvatar] = useState(null);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [image, setImage] = useState(null);


  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setAvatar(e.target.files[0]);
    }
  };

  const validatePassword = (pwd) => {
    if (pwd.length < 6) return "M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±";
    if (!/[A-Z]/.test(pwd)) return "M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 ch·ªØ in hoa (A-Z)";
    if (!/[a-z]/.test(pwd)) return "M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 ch·ªØ th∆∞·ªùng (a-z)";
    if (!/[!@#$%^&*(),.?":{}|<>]/.test(pwd))
      return "M·∫≠t kh·∫©u ph·∫£i ch·ª©a √≠t nh·∫•t 1 k√Ω t·ª± ƒë·∫∑c bi·ªát";
    return null;
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!name || !email || !password || !confirmPassword || !phone || !avatar) {
      setError("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin v√† ch·ªçn ·∫£nh ƒë·∫°i di·ªán");
      return;
    }

    const pwdError = validatePassword(password);
    if (pwdError) {
      setError(pwdError);
      return;
    }

    if (password !== confirmPassword) {
      setError("M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp");
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("email", email);
      formData.append("password", password);
      formData.append("confirmPassword", confirmPassword);
      formData.append("phone", phone);
      if (image) {
  formData.append("image", image); // ‚úÖ ƒë√∫ng t√™n field
}

     await register(formData);  
     alert("ƒêƒÉng k√Ω th√†nh c√¥ng!");

      setSuccess("ƒêƒÉng k√Ω th√†nh c√¥ng!");
      setError("");
      setTimeout(() => {
        window.location.href = "/"; // chuy·ªÉn sang login
      }, 1500);
    } catch (err) {
      setError(err.message || "ƒêƒÉng k√Ω th·∫•t b·∫°i");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white flex flex-col">
      {/* Background ƒë·ªè */}
      <div className="bg-gradient-to-b from-red-800 to-red-600 h-[35vh]"></div>

      {/* Form tr·∫Øng */}
      <div className="bg-white rounded-t-[40px] shadow-lg -mt-16 p-8 max-w-md mx-auto w-full">
        {/* Logo */}
        <div className="text-center mb-6">
          <h1 className="text-3xl font-bold text-red-600">SeatMe</h1>
          <p className="text-red-600 text-sm tracking-widest">ƒê·∫∂T B√ÄN ONLINE</p>
        </div>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        {success && <p className="text-green-600 text-center mb-4">{success}</p>}

        <form onSubmit={handleRegister} className="space-y-5">
          <div>
            <label className="block text-red-600 mb-1">H·ªç t√™n</label>
            <input
              type="text"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-red-600 mb-1">Email</label>
            <input
              type="email"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-red-600 mb-1">M·∫≠t kh·∫©u</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button
                type="button"
                className="absolute right-3 top-2 text-red-600"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
          </div>

          <div>
            <label className="block text-red-600 mb-1">X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
            <div className="relative">
              <input
                type={showConfirmPassword ? "text" : "password"}
                className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
              <button
                type="button"
                className="absolute right-3 top-2 text-red-600"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              >
                {showConfirmPassword ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
          </div>

          <div>
            <label className="block text-red-600 mb-1">S·ªë ƒëi·ªán tho·∫°i</label>
            <input
              type="tel"
              className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:border-red-500 outline-none"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-red-600 mb-1">·∫¢nh ƒë·∫°i di·ªán</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="w-full"
            />
            {avatar && (
              <img
                src={URL.createObjectURL(avatar)}
                alt="avatar preview"
                className="w-20 h-20 rounded-full mt-2 object-cover"
              />
            )}
          </div>

          <button
            type="submit"
            className="w-full bg-gradient-to-r from-red-800 to-red-600 text-white font-bold py-3 rounded-full"
            disabled={loading}
          >
            {loading ? "ƒêang ƒëƒÉng k√Ω..." : "ƒêƒÉng k√Ω"}
          </button>
        </form>

        <p className="text-center text-gray-600 mt-6">
          B·∫°n ƒë√£ c√≥ t√†i kho·∫£n?{" "}
          <a href="/" className="font-bold text-black">
            ƒêƒÉng nh·∫≠p
          </a>
        </p>
      </div>
    </div>
  );
};

export default Register;
