import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [snackbar, setSnackbar] = useState({ show: false, message: "", error: true });

  const navigate = useNavigate();

  const handleLogin = async () => {
    if (!email || !password) {
      showSnackbar("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß email v√† m·∫≠t kh·∫©u");
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post("http://192.168.228.138:5000/api/auth/login", {
        email,
        password,
      });

      if (response.status === 200) {
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("user", JSON.stringify(response.data.user));
        showSnackbar("ƒêƒÉng nh·∫≠p th√†nh c√¥ng!", false);

        setTimeout(() => {
          navigate("/dashboard");
        }, 1000);
      } else {
        showSnackbar(response.data.message || "Email ho·∫∑c m·∫≠t kh·∫©u kh√¥ng ƒë√∫ng");
      }
    } catch (error) {
      showSnackbar("L·ªói k·∫øt n·ªëi m√°y ch·ªß: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const showSnackbar = (message, error = true) => {
    setSnackbar({ show: true, message, error });
    setTimeout(() => setSnackbar({ show: false, message: "", error: true }), 2000);
  };

  return (
    <div className="relative min-h-screen bg-white">
      {/* Red gradient background */}
      <div className="absolute top-0 left-0 w-full h-1/3 bg-gradient-to-b from-red-900 to-red-600"></div>

      <div className="relative flex justify-center items-start pt-32">
        <div className="w-full max-w-md bg-white rounded-t-3xl shadow-lg p-8">
          {/* Logo */}
          <div className="text-center mb-8">
            <h1 className="text-3xl font-bold text-red-600">SeatMe</h1>
            <p className="text-xs tracking-widest text-red-600">ƒê·∫∂T B√ÄN ONLINE</p>
          </div>

          {/* Email input */}
          <div className="mb-4">
            <label className="block text-red-600 font-semibold mb-1">Email</label>
            <input
              type="email"
              className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:border-red-500"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Nh·∫≠p email"
            />
          </div>

          {/* Password input */}
          <div className="mb-4">
            <label className="block text-red-600 font-semibold mb-1">M·∫≠t kh·∫©u</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:border-red-500"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-3 text-red-600"
              >
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
          </div>

          {/* Forgot password */}
          <div className="flex justify-end mb-6">
            <button
              className="text-red-500 font-bold text-sm"
              onClick={() => navigate("/forgot-password")}
            >
              Qu√™n m·∫≠t kh·∫©u
            </button>
          </div>

          {/* Login button */}
          {loading ? (
            <div className="flex justify-center mb-6">
              <div className="loader border-t-4 border-red-600 rounded-full w-8 h-8 animate-spin"></div>
            </div>
          ) : (
            <button
              onClick={handleLogin}
              className="w-full bg-gradient-to-r from-red-900 to-red-600 text-white py-3 rounded-full font-bold text-lg shadow-lg hover:opacity-90"
            >
              ƒêƒÉng nh·∫≠p
            </button>
          )}

          {/* Register link */}
          <div className="text-center mt-6">
            <span className="text-gray-500">B·∫°n ch∆∞a c√≥ t√†i kho·∫£n? </span>
            <button
              className="font-bold text-black"
              onClick={() => navigate("/register")}
            >
              ƒêƒÉng k√Ω
            </button>
          </div>
        </div>
      </div>

      {/* Snackbar */}
      {snackbar.show && (
        <div
          className={`fixed bottom-5 left-1/2 transform -translate-x-1/2 px-6 py-3 rounded-lg text-white font-bold ${
            snackbar.error ? "bg-red-600" : "bg-green-600"
          }`}
        >
          {snackbar.message}
        </div>
      )}
    </div>
  );
}
